/*
Square Component
*/
import React from 'react'

class Square extends React.Component{

    constructor(prop){
        super(prop);
        this.state = {
            value: null,
        };
    }

    render(val){
        return(
            <button 
                className="square" 
                onClick={() => this.setState({value:'X'})} 
            >
                {this.state.value}
            </button>
        )
    }
}

export default Square;
