import React from 'react'


class Toggle extends React.Component{
    constructor(props){
        super(props);
        this.state = {isOn:true};
    }

    render(){
        return(<button
            onClick={this.handleClick}>
            {this.state.isOn ? 'ON':'OFF'}
        </button>);
    }

    handleClick(){
        this.setState(state => {isOn:!this.state.isOn});
    }
}

export default Toggle;
